document.getElementById("cards").onmousemove = e => {
    for (const t of document.getElementsByClassName("card")) {
        const o = t.getBoundingClientRect(),
            s = e.clientX - o.left,
            n = e.clientY - o.top;
        t.style.setProperty("--mouse-x", `${s}px`), t.style.setProperty("--mouse-y", `${n}px`)
    }
};